          <input type="text" name="password" placeholder="Senha" onChange={(e)=>{
            setPassword(e.target.value)
          }}></input>
          <button className="buttonEnter" onClick={temp}>Entrar</button>
        </div>
      </div>
    );
  }

export default Login;
