import React, {useEffect, useState} from 'react';
import {Chart} from 'react-google-charts'
import _ from 'lodash'
import Axios from 'axios'
import {AiFillHome} from "react-icons/ai";
import {Link} from 'react-router-dom'; 

  export const options_uti = {
    title: "UTI",
  };

  export const options_enfermaria = {
    title: "Enfermaria",
  };

  export const options_total = {
    title: "Total",
  };


function Leitos () {
    const [leitosVagas, setLeitosVagas] = useState([])
    const totalVagas = _.sumBy(leitosVagas, function(o) {return o.qte})

    const vagasUTI = leitosVagas[0]
    const vagasEnferaria = leitosVagas[1]

    const data_uti = [
      ["Situação", "Quantidade"],
      ["Live", vagasUTI],
      ["Ocupado", 10],
    ];
    const data_enfermaria = [
      ["Situação", "Quantidade"],
      ["Live", vagasEnferaria],
      ["Ocupado", 15],
    ];
    const data_total = [
      ["Situação", "Quantidade"],
      ["Live", totalVagas],
      ["Ocupado", 25],
    ];

    useEffect(() => {
      Axios.get("http://localhost:3001/leitos").then((response) =>{
        setLeitosVagas(response.data)
      })
    }, [])

    return(
        <div className='leitos'>
            <header>
                <Link to="/home"><AiFillHome size={25} color="#FFF"/></Link>
                <h2>Página de Leitos</h2>
            </header>
            <div className='charts'>
                <Chart
                chartType="PieChart"
                data={data_uti}
                options={options_uti}
                width={"100%"}
                height={"400px"}
                />
                <Chart
                chartType="PieChart"
                data={data_enfermaria}
                options={options_enfermaria}
                width={"100%"}
                height={"400px"}
                />
                <Chart
                chartType="PieChart"
                data={data_total}
                options={options_total}
                width={"100%"}
                height={"400px"}
                />
            </div>
        </div>
    );
}

export default Leitos;
